import{_ as t,c as s,o as e,ag as r}from"./chunks/framework.KLbWPeN7.js";const i="/assets/hrsthrt74%20Misty%20Ver.Bfh_bZfE.jpg",o="/assets/HyperOS%202%20Settings.Z-VGs13J.jpg",p="/assets/Text%20Border%20Texture.D9NfM78x.png",n="/assets/hrsthrt74%20Misty%20Ver%20DEMO.CHaXEXcA.jpg",c="/assets/hrsthrt74%20Misty%20Ver%20PROMO.B7t7DjXz.jpg",l="/assets/ss74%20Misty%20Ver.JTODcvcj.jpg",h="/assets/%E9%85%B7%E5%AE%89%20%E5%8D%83%E7%B2%89%20%E5%AE%A3%E4%BC%A0%E5%9B%BE.CsuAuYQL.jpg",m="/assets/hrsthrt74%20Misty%20Ver%20%E5%90%A7%E5%94%A7.Dtt5zn3l.png",d="/assets/Badge%20Photo.Dazp464i.jpg",f=JSON.parse('{"title":"Misty Ver.","description":"","frontmatter":{},"headers":[],"relativePath":"docs/design/avatar/misty_ver.md","filePath":"docs/design/avatar/misty_ver.md","lastUpdated":1751736502000}'),_={name:"docs/design/avatar/misty_ver.md"};function u(g,a,y,b,B,E){return e(),s("div",null,a[0]||(a[0]=[r('<h1 id="misty-ver" tabindex="-1">Misty Ver. <a class="header-anchor" href="#misty-ver" aria-label="Permalink to &quot;Misty Ver.&quot;">​</a></h1><p><img src="'+i+'" alt="Misty Ver. Avatar"></p><h6 id="_2024-12-21" tabindex="-1">2024/12/21 <a class="header-anchor" href="#_2024-12-21" aria-label="Permalink to &quot;2024/12/21&quot;">​</a></h6><h2 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to &quot;简介&quot;">​</a></h2><p>本次的头像以 HyperOS 2 的设置页面为主题，并加以改编。</p><details class="details custom-block"><summary>HyperOS 2 的设置页面是？</summary><p><img src="'+o+'" alt="Screenshot"></p></details><p>因为上一版头像已经用了很长时间了（10 个月），也正值 HyperOS 2 发布，很喜欢这个设置界面的 UI，于是做了。</p><hr><p>面临的第一个问题是，因为这次想做更高级的效果，PPT 这时就捉襟见肘了，于是本次将工程迁移到了 PS，虽然只迁移了一半。。。。</p><p>配色上并没有全盘参考设置的配色，也做出了一点点改动。<s>但其实都是PS里的预设</s></p><p>背景的渐变是怎么做的呢？其实也很简单，就是四个椭圆，然后再加上模糊滤镜，欸这不就成了（）</p><details class="details custom-block"><summary>朴实无华的背景效果</summary><p><img src="'+p+'" alt="Avatar Background"></p></details><p>先做好一张底图，然后前后景就都可以在这张图的基础上改编了。</p><p>制作的过程中也学到了，可以通过改饱和度和亮度来控制显示效果，比如文字填充部分亮一点，颜色淡一点；描边则颜色鲜明一些</p><hr><p>差不多做好了，自我感觉良好。发给朋友一看，他却觉得很怪，怎么会是呢？</p><details class="details custom-block"><summary>原来是...？</summary><p><strong>前后景的图片没对齐！！</strong><img src="'+n+'" alt="Avatar DEMO"> 不过我真觉得还不错欸（小声）</p></details><p>对齐了之后，再加点小阴影，完成！</p><details class="details custom-block"><summary>阴影也有说道</summary><p>之前的 Ave. Ver. 踩过一个大坑，就是没有考虑到头像缩到非常小之后的观感。</p><p>于是这次就花了不少时间在调阴影上，确保缩到很小后观感也能不错。</p></details><hr><p>不过名字叫什么呢？总不能直接照抄 HyperOS 吧？<s>（这也要抄？）</s></p><p>看着这弥散的渐变，想到了雾——嗯，那就 Misty Ver. 吧！</p><hr><h1 id="banner" tabindex="-1">Banner <a class="header-anchor" href="#banner" aria-label="Permalink to &quot;Banner&quot;">​</a></h1><p><img src="'+c+'" alt="Banner"> 本次的 Banner 没有采用传统样式，究其原因不是为了创新，反而是因为把 Banner 的工程迁移到 PS 也是不小的工作量，于是摆烂了。</p><hr><h1 id="变体" tabindex="-1">变体 <a class="header-anchor" href="#变体" aria-label="Permalink to &quot;变体&quot;">​</a></h1><h3 id="spacestation-74th-群头像" tabindex="-1">SpaceStation 74th 群头像 <a class="header-anchor" href="#spacestation-74th-群头像" aria-label="Permalink to &quot;SpaceStation 74th 群头像&quot;">​</a></h3><p><img src="'+l+'" alt="SpaceStation 74th Icon"> 完全保持了原头像的模板，仅仅是换个色。</p><h3 id="酷安千粉宣传图" tabindex="-1">酷安千粉宣传图 <a class="header-anchor" href="#酷安千粉宣传图" aria-label="Permalink to &quot;酷安千粉宣传图&quot;">​</a></h3><p><img src="'+h+'" alt="酷安千粉宣传图"></p><h3 id="徽章-吧唧" tabindex="-1">徽章（吧唧） <a class="header-anchor" href="#徽章-吧唧" aria-label="Permalink to &quot;徽章（吧唧）&quot;">​</a></h3><p><img src="'+m+'" alt="Badge Source Image"> 好在背景图够大，足够给徽章的<a href="https://zh.wikipedia.org/wiki/%E5%87%BA%E8%A1%80_(%E5%8D%B0%E5%88%B7)" target="_blank" rel="noreferrer">出血</a>留出地方。</p><p>成品在字体填充部分还做了烫银处理，效果非常不错！ <img src="'+d+'" alt="Badge Photo"></p>',34)]))}const P=t(_,[["render",u]]);export{f as __pageData,P as default};
